import React, {useEffect, useState, Fragment} from 'react';
import './App.css';

const App  = () => {
const [state, setState] = useState({data: []});

useEffect(()=>{
    fetchData();
}, [state]);

const fetchData =  async () => {
    try {
        const response = await fetch('https://127.0.0.1:2999/liveclientdata/allgamedata');
        if (!response.ok) {throw Error(response.statusText);}
        const json = await response.json();
        console.log(json);
        setState({ data: json, timer: JSON.stringify(json.gameData.gameTime), 
          champName: JSON.stringify(json.allPlayers[0].championName), playerName: JSON.stringify(json.allPlayers[0].summonerName
        )});

    }
    catch (error) {console.log(error);}
}

return (
  <Fragment>
    <ul>
      <li> {state.timer} </li>
      <li> Player 1
        <ul>
          <li> {state.playerName} </li>
          <li> {state.champName} </li>
        </ul> 
      </li>
    </ul>
  </Fragment>
);
}

export default App;
