import React, {useEffect, useState, Fragment} from 'react';
import './App.css';

const URL = 'https://127.0.0.1:2999';
const ENDPOINT = "/liveclientdata/allgamedata"; 
const App  = () => {
const [state, setState] = useState({data: []});

useEffect(()=>{
    fetchData();
}, [state]);

const fetchData =  async () => {
    try {
      /*
        const response = await fetch('https://127.0.0.1:2999/liveclientdata/allgamedata');
        if (!response.ok) {throw Error(response.statusText);}
        const json = await response.json();
        setState({ data: json, timer: JSON.stringify(json.gameData.gameTime), 
          allPlayers: JSON.stringify(json.allPlayers)});
        console.log(json);
        */
       const response = await fetch(URL + ENDPOINT);
        if (!response.ok) {throw Error(response.statusText);}
        const json = await response.json();
        setState({ data: json, timer: JSON.stringify(json.gameData.gameTime), 
          sumName: (json.allPlayers[9].summonerName),
          champName: (json.allPlayers[9].championName),
          sumScores: JSON.stringify(json.allPlayers[9].scores)

        });
        console.log(json);
    }
    catch (error) {console.log(error);}
}

//console.log(state.allPlayers);

return (
  <Fragment>
 {state.timer}
    <ul>{state.sumName}
    <li><img src={"C:\Users\PRD01\Desktop\Draft\web\img\icon\champion\icon_"+ state.champName + ".png"}/></li>
      <li>{state.champName}</li>

      <li>
          {state.sumScores}
        </li>

    </ul>
  </Fragment>
);
}

export default App;
